import type { ThankYouModuleType } from './thankYouModule';

export const getThankYouModuleIcon = (
	moduleType: ThankYouModuleType,
): JSX.Element => {
	switch (moduleType) {
		case 'appDownload':
			return <DownloadTheAppIcon size={38} />;
		case 'feedback':
			return <FeedbackIcon />;
		case 'socialShare':
			return <ShareIcon />;
		case 'signUp':
		case 'signIn':
			return <PersonWithTickIcon />;
		case 'marketingConsent':
			return <NotificationIcon />;
		case 'ausMap':
			return <AusMapIcon />;
		case 'supportReminder':
			return <SupportReminder />;

		/////////////////////////
		// organise ICONS //
		/////////////////////////
		default:
			return <SvgWrapper />;
	}
};

interface SvgWrapperProps {
	size?: number;
	children?: React.ReactNode;
}

function SvgWrapper(props: SvgWrapperProps) {
	return (
		<svg
			width={props.size ?? '38'}
			height={props.size ?? '38'}
			viewBox="0 0 38 38"
			fill="none"
			xmlns="http://www.w3.org/2000/svg"
			preserveAspectRatio="xMinYMin meet"
		>
			{props.children}
		</svg>
	);
}

