import type { ThankYouModuleType } from './thankYouModule';

export const getThankYouModuleIcon = (
	moduleType: ThankYouModuleType,
): JSX.Element => {
	switch (moduleType) {
		case 'appsDownload':
			return <DownloadTheAppsIcon />;
		case 'appDownload':
			return <DownloadTheAppIcon size={35} />;
		case 'ausMap':
			return <AusMapIcon />;
		case 'feedback':
			return <FeedbackIcon />;
		case 'signIn':
		case 'signUp':
			return <PersonWithTickIcon />;
		case 'socialShare':
			return <ShareIcon />;
		case 'supportReminder':
			return <SupportReminder />;

		default:
			return <SvgWrapper />;
	}
};

interface SvgWrapperProps {
	size?: number;
	children?: React.ReactNode;
}

function SvgWrapper(props: SvgWrapperProps) {
	return (
		<svg
			width={props.size ?? '38'}
			height={props.size ?? '38'}
			viewBox="0 0 38 38"
			fill="none"
			xmlns="http://www.w3.org/2000/svg"
			preserveAspectRatio="xMinYMin meet"
		>
			{props.children}
		</svg>
	);
}

